#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int k) {
         int n = nums.size();
        ll res = 0;
        unordered_map<ll, ll> mp;
        for (int i = 0; i < n; ++i) {
            unordered_map<ll, ll> cr; 
            if (nums[i] == k) {
                ++res;
            }
            cr[nums[i]] = 1;
            for (auto& [v, f] : mp) {
                ll temp = v & nums[i];
                if (temp == k) {
                    res += f;
                }
                cr[temp] += f;
            }

            mp = cr;
        }
        return res;
    }
};
