class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
         int size = nums.size();
        long long arrcnt = 0;

        unordered_map<long long, long long> res;

        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> curres; 
            curres[nums[i]] = 1;
            if (nums[i] == k) {
                ++arrcnt;
            }


            for (auto& [prevval, freq] : res) {
                long long newres = prevval & nums[i];
                curres[newres] += freq;
                if (newres == k) {
                    arrcnt += freq;
                }  
            }

            res = curres;
        }

        return arrcnt;
    }
};
