class Solution {
public:
    long long countSubarrays(vector<int>& n, int k) {
        int m = n.size();
        long long ans = 0;

        unordered_map<long long, long long> prev;

        for (int i = 0; i < m; ++i) {
            unordered_map<long long, long long> curr; 

            if (n[i] == k) {
                ++ans;
            }
            curr[n[i]] = 1;

            for (auto& [val, freq] : prev) {
                long long newAnd = val & n[i];
                if (newAnd == k) {
                    ans += freq;
                }
                curr[newAnd] += freq;
            }

            prev = curr;
        }

        return ans;
    }
};
