class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        long long curr= 0;
        unordered_map<long long,long long> mp;
        for(auto it:nums){
            unordered_map<long long,long long> mp1;
            if(it == k)ans++;
            mp1[it] = 1;
            for(auto &i:mp){
                long long x = i.first&it;
                if(x == k)ans+=i.second;
                mp1[x] += i.second;
            }
            mp = mp1;
        }
        return ans;
    }
};
