class Solution
{
public:
    #define umll unordered_map<long long, long long>
    long long countSubarrays(vector<int> &nums, int k)
    {
        int n = nums.size();
        long long ct{};

        umll pr;
        long long ans = 0ll;
        for (int i{}; i < n; i+=1)
        {
            umll cr;

            if (nums[i] == k) ct += max(ans, 1ll);

            cr[nums[i]] = max(ans, 1ll);

            for (auto it : pr)
            {
               long long val = it.first;
                long long freq = it.second ;
                long long nr = val & nums[i];
                if (nr == k)  ct += freq;
                cr[nr] += freq;
            }
            pr = cr;
        }

        return ct;
    }
};
