class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        long long ans=0;

        unordered_map<long long,long long> pr;
        long long n=nums.size();
        for (int i =0;i<n;i++) {

            unordered_map<long long,long long> cu; 
             cu[nums[i]]=1;


            if (nums[i] == target) {
                ans++;
            }

            for (auto p: pr){


                long long temp= p.first & nums[i];
                if (temp==target) {
                    ans=ans+p.second;
                }


                cu[temp]=cu[temp]+p.second;
            }


            pr= cu;
        }

        return ans;
    }
};
