class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        long long c = 0;

        unordered_map<long long, long long> prevmap;
         int n = nums.size();

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currmap; 

            if (nums[i] == k) {
                c++;
            }

            currmap[nums[i]] = 1;

            for (auto& [And, freq] : prevmap) {
//                 newand nikala
                long long newand = And & nums[i];

                if (newand == k) {
                    c += freq;
                }

                currmap[newand] += freq;
            }

            prevmap = currmap;
        }

        return c;
    }
};
