def bits(n):
    for i in range(n.bit_length()):
        if (1<<i)&n:
            yield i
class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        i = -1
        good = {}
        total = 0
        bs = list(set(range(max(n.bit_length() for n in nums)))-set(bits(k)))
        for j,n in enumerate(nums):
            if k&n != k:
                i = j
                good = {}
            else:
                for b in bs:
                    if not (1<<b)&n:
                        good[b] = j
                if len(good) == len(bs):
                    total += min(good.values(),default=j)-i
        return total
