class Solution {
public:
    long long solve(vector<int>& v, int k) {
        long long int ct = 0;
        unordered_map<int, int> mapofAND;

        for (int num : v) {
            unordered_map<int, int> newAndMap;
            newAndMap[num]++; 
            for (const auto& [valueofand, frequency] : mapofAND) {
                int newandd = valueofand & num;
                newAndMap[newandd] += frequency;
            }
            for (const auto& [valueofand, frequency] : newAndMap) {
                if (valueofand == k) {
                    ct += frequency;
                }
            }
            mapofAND = newAndMap;
        }
        return ct;
    }
    long long countSubarrays(vector<int>& v, int k) {
        return solve(v, k);
    }
};
