class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        long long int res=0;
        int n=arr.size();
        unordered_map<long long int ,long long int>mp1;
        for(int i=0;i<n;i++){
            unordered_map<long long int,long long int>mp2;
            if(arr[i]==k){
                ++res;

            }
            mp2[arr[i]]=1;
            for(auto &[val1,cnt]:mp1){
                long long int res1=val1&arr[i];
                if(res1==k){
                    res+=cnt;
                }
                mp2[res1]+=cnt;
            }
            mp1=mp2;
        }
        return res;
    }
};
