typedef vector<int> vec;
typedef unordered_map<string, long long> mp;
typedef unordered_map<long long, long long> mpp;

#define f(t,n) for (int t = 0; t < n; t++) 
#define fr(val,cn,rp) for (auto& [val, cn] : rp)
class Solution {
public:
    ll countSubarrays(vector<int>& box, int res) {
        int size = box.size();
        ll whole = 0;
        mpp rp;
        f(t,size) {
            unordered_map<ll, ll> rc; 
            if (box[t] == res) {
                whole++;
            }
            rc[box[t]] = 1;
            fr(value,cn,rp) {
                ll nrt = value & box[t];
                if (nrt == res) {
                    whole += cn;
                }
                rc[nrt] += cn;
            }

            rp = rc;
        }

        return whole;
    }
};
