class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        unordered_map<long long,long long> result;

        for(int i=0;i<n;i++){
            unordered_map<long long,long long> temp;

            if(nums[i] == k){
                count++;
            }

            temp[nums[i]] = 1;

            for(auto [value,freq]:result){
                long long currAns = value & nums[i];
                if(currAns == k){
                    count += freq;
                }
                temp[currAns] += freq;
            }
            result = temp;
        }    
        return count;

    }
};
