class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> mp;
        for (int index = 0; index < n; ++index) {
            unordered_map<long long, long long> cmp; 
            if (nums[index] == k) {
                ++ans;
            }
            cmp[nums[index]] = 1;
            for (auto& [value, frequency] : mp) {
                long long res = value & nums[index];
                if (res == k) {
                    ans += frequency;
                }
                cmp[res] += frequency;
            }

            mp = cmp;
        }
        return ans;
    }
};
