class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<long long, long long> acm;
        long long res = 0;

        for (long long i = 0; i < n; ++i) {
            unordered_map<long long, long long> cam;
            for (auto &entry : acm) {
                long long newAndValue = entry.first & nums[i];
                cam[newAndValue] += entry.second;
            }
            cam[nums[i]]++;
            acm = cam;

            if (acm.find(k) != acm.end()) {
                res += acm[k];
            }
        }

        return res;
    }
};
