class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        long long dev = 0;
        unordered_map<int, int> mp1;
        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> mp2;
            mp2[a[i]]++;
            for (auto& pair : mp1) {
                int x = pair.first & a[i];
                mp2[x] += pair.second;
            }
            mp1 = mp2;
            dev += mp1[k];
        }
        return dev;

    }
};
