class Solution {
public:
    long long countSubarrays(vector<int>& myNums, int myK) {
        int n = myNums.size();
        long long myans = 0;
        unordered_map<long long, long long> outerMP;

        for (int idx = 0; idx < n; ++idx) {
            unordered_map<long long, long long> innerMP; 
            if (myNums[idx] == myK) {
                ++myans;
            }

            innerMP[myNums[idx]] = 1;
            for (auto& [item, freq] : outerMP) {
                long long nextResult = item & myNums[idx];
                if (nextResult == myK) {
                    myans += freq;
                }
                innerMP[nextResult] += freq;
            }

            outerMP = innerMP;
        }

        return myans;
    }
};
