class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        long long all_cnt = 0;

        unordered_map<long long, long long> past_score;

        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> first_score; 

            if (nums[i] == k) {
                ++all_cnt;
            }
            first_score[nums[i]] = 1;

            for (auto& [score, gain] : past_score) {
                long long requird_score = score & nums[i];
                if (requird_score == k) {
                    all_cnt += gain;
                }
                first_score[requird_score] += gain;
            }

            past_score = first_score;
        }

        return all_cnt;
    }
};
