class Solution {
public:
    long long solve(vector<int> &v,int k){
        if(k<0)return 0;
        vector<int> bits(31,0);
        long long ans=0;
        int val=v[0],j=0,n=v.size();
        for(int i=0;i<n;i++){
            val&=v[i];
            // cout<<val<<endl;
            int tmp=v[i],cnt=0;
            while(tmp){
                if(tmp&1)bits[cnt]++;
                tmp/=2;
                cnt++;
            }
            while(j<=i && val<k){
                val=0;
                for(int l=0;l<31;l++){
                    if((v[j]>>l)&1)bits[l]--;
                    if(bits[l]==i-j)val+=pow(2,l);
                }
                j++;
            }
            if(val>=k)ans+=i-j+1;

        }
        return ans;
    }

    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=solve(nums,k)-solve(nums,k+1);
        // cout<<solve(nums,k)<<endl;
        return ans;
    }
};
