#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll n=nums.size();
        ll ans=0;
        map<ll,ll> mpp1;
        for (int i=0; i<n; i++)
        {
            map<ll,ll> mpp2; 
            if (nums[i]==k)
            ans++;
            mpp2[nums[i]]=1;
            for (auto pr:mpp1)
            {
                ll ne=(pr.first & nums[i]);
                if (ne==k)
                ans+=pr.second;
                mpp2[ne]+=pr.second;
            }
            mpp1=mpp2;
        }
        return ans;
    }
};
