public class Solution {
    public long CountSubarrays(int[] nums, int k) {
        int[] p = new int[32];
        for (int i = 0; i < p.Length; i++) {
            p[i] = -1;
        }
        long result = 0;
        for (int i = 0; i < nums.Length; i++) {
            var n = nums[i];
            for (int j = 0; j < 32; j++) {
                if ((n & 1) == 0) {
                    p[j] = i;
                }
                n >>= 1;
            }
            int l = 0;
            int r = i;
            int kk = k;
            for (int j = 0; j < 32; j++) {
                if ((kk & 1) == 0) {
                    r = Math.Min(r, p[j]);
                } else {
                    l = Math.Max(l, p[j] + 1);
                }
                kk >>= 1;
            }
            result += Math.Max(r - l + 1, 0);
        }
        return result;
    }
}
