class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        map<int, int> mp;
        long long result = 0;

        for (int i = 0; i < a.size(); i++) {
            map<int, int> newMp;

            for (auto& e : mp) {
                int newAnd = a[i] & e.first;
                newMp[newAnd] += e.second;
            }

            newMp[a[i]]++;

            mp = newMp;

            if (mp.find(k) != mp.end()) {
                result += mp[k];
            }
        }

        return result;

    }
};
