class Solution {
public:
  long long countSubarrays(vector<int>& nums, int k) {
    int n = nums.size();
    vector<vector<int>> o(32, vector<int>(n + 1, n));
    vector<vector<int>> z(32, vector<int>(n + 1, n));
    for (int i = n; i-- > 0; ) {
      for (int j = 0; j < 32; ++j) {
        if ((nums[i] >> j) & 0x01) {
          o[j][i] = o[j][i + 1] + 1;
          z[j][i] = z[j][i + 1];
        } else {
          o[j][i] = 0;
          z[j][i] = i;
        }
      }
    }
    long long ret = 0;
    for (int i = 0; i < n; ++i) {
      int l = 0, r = n - i;
      for (int j = 0; j < 32; ++j) {
        if ((k >> j) & 0x01) {
          r = min(r, o[j][i]);
        } else {
          l = max(l, z[j][i]);
        }
      }
      if (r > l - i) ret += r - (l - i);
    }
    return ret;
  }
};
