from collections import defaultdict

class Solution:
    def countSubarrays(self, nums: List[int], target: int) -> int:
        size = len(nums)
        total_subarrays = 0
        prefix_and_counts = defaultdict(int)

        for index in range(size):
            current_and_counts = defaultdict(int)
            if nums[index] == target:
                total_subarrays += 1
            current_and_counts[nums[index]] = 1

            for prefix_and, prefix_count in prefix_and_counts.items():
                new_and_result = prefix_and & nums[index]
                if new_and_result == target:
                    total_subarrays += prefix_count
                current_and_counts[new_and_result] += prefix_count

            prefix_and_counts = current_and_counts

        return total_subarrays
