class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        pre = [ [0]*32 for i in range(n+1) ]
        for i in range(n):
            for j in range(32):
                pre[i+1][j] = pre[i][j] +  (1  if ((1 << j) & nums[i]) != 0 else 0  )

        def value(i,j):
            v = 0
            for b in range(32):
                if pre[j+1][b] - pre[i][b] == j-i+1:
                    v |= (1 << b)
            return v 

        # print("---"*n)
        res = 0
        r1,r2 = 0,0
        for l in range(n):
            r1 = max(l,r1)
            w = value(l,r1)
            if w < k:
                continue 
            while r1 < n-1 and w > k:
                r1 += 1
                w = value(l,r1)
            if w != k:
                continue 
            # print("valid", l , r1)
            r2 = max(r2, r1)
            while r2 < n-1 and w >= k:
                r2 += 1
                w = value(l,r2)
            if w != k:
                r2 -= 1
            res += r2-r1 + 1
        return res
