class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ans = 0
        and_map = defaultdict(int)
        for num in nums:
            current_and = num
            if current_and == k:
                ans += 1
            data = defaultdict(int)
            for key, value in and_map.items():
                new_and = key & num
                if new_and == k:
                    ans += value
                data[new_and] += value
            data[current_and] += 1
            and_map = data
        return ans
