class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int a1=-1;
        long long int ans=0;
        int prev;
        map<int,int> m1;
        for(int i=0;i<nums.size();i++){

            if((nums[i]&k)==k){
                if(prev==-1){
                    prev=i;
                }
                int r1=i;
                int flag=1;
                for(int x=0;x<30;x++){
                    if(((1<<x)&(nums[i]))!=0&&((1<<x)&(k))==0){

                        if(m1.find(x)!=m1.end()){
                            r1=min(r1,m1[x]);
                        }
                        else{
                            flag=2;
                        }

                    }
                    if(((1<<x)&(nums[i]))==0){
                        m1[x]=i;
                    }
                }
                if(flag==1){
                    ans=ans+(r1-prev+1);
                }
            }
            else{
                prev=-1;
                m1.clear();
                a1=-1;
            }
        }
        return ans;
            }
};
