class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int arraySize = array.size();
        long long totalCount = 0;
        unordered_map<long long, long long> previousSubarrays;
        for (int index = 0; index < arraySize; ++index) {
            unordered_map<long long, long long> currentSubarrays; 
            if (array[index] == target) {
                ++totalCount;
            }
            currentSubarrays[array[index]] = 1;
            for (auto& [value, frequency] : previousSubarrays) {
                long long newAndResult = value & array[index];
                if (newAndResult == target) {
                    totalCount += frequency;
                }
                currentSubarrays[newAndResult] += frequency;
            }

            previousSubarrays = currentSubarrays;
        }
        return totalCount;
    }
};
