class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>>v(n,vector<int>(33,0));
        for(int j=0; j<=32; j++){
            int la=n-1;
            for(int i=n-1; i>=0; i--){
                if(nums[i]&1){
                    v[i][j]=la;
                }
                else{
                    la=i-1;
                    v[i][j]=la;
                }
                nums[i]=nums[i]>>1;
            }
        }
        long long int ans=0;
        for(int l=0; l<n; l++){
            int mi=l,ma=n-1;
            int temp=k;
            for(int i=0; i<=32; i++){
                if(temp&1){
                    ma=min(ma,v[l][i]);
                }
                else{
                    mi=max(mi,v[l][i]+1);
                }
                temp=temp>>1;
            }
            if(mi<=ma){
                ans=ans+ma-mi+1;
            }
        }
        return ans;
    }
};
