from typing import List
from collections import defaultdict

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        s = len(nums)
        total = 0 
        prev = defaultdict(int)
        for index in range(s):
            curr = defaultdict(int)
            if nums[index] == k:
                total += 1        
            curr[nums[index]] = 1
            for val, freq in prev.items():
                new = val & nums[index]
                if new == k:
                    total += freq
                curr[new] += freq  
            prev = curr
        return total
