#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll ans = 0;
        ll n = nums.size();
        unordered_map<ll, ll>m1;
        for(int i=0;i<n;i++){
            unordered_map<ll,ll>m2;
            int curr = nums[i];
            if(curr==k) ans++;
            m2[nums[i]]=1;
            for(auto it : m1){
                int temp = it.first;
                int cnt = it.second;
                int newcurr = curr&temp;
                if(newcurr==k) ans+=cnt;
                m2[newcurr]+=cnt;
            }
            m1=m2;
        }
        return ans;
    }
};
