class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long result = 0;

        unordered_map<long long, long long> previousResults;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currentResults; 

            if (nums[i] == k) {
                ++result;
            }
            currentResults[nums[i]] = 1;

            for (auto& [value, frequency] : previousResults) {
                long long newAndResult = value & nums[i];
                if (newAndResult == k) {
                    result += frequency;
                }
                currentResults[newAndResult] += frequency;
            }

            previousResults = currentResults;
        }

        return result;
    }
};
