class Solution {
public:
    #define ll long long 
    long long solve(vector<int>& nums, int kk) {
    unordered_map<ll,ll>mp;
        ll ans=0;
        for(auto t:nums){
            unordered_map<ll,ll>mp2;mp2[t]++;
            for(auto [l,m]:mp){
                mp2[t&l]+=m;
            }
            mp=mp2;
             for(auto [l,m]:mp){
                if(l==kk)ans+=m;
            }

        }
        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        return solve(nums,k);
    }
};
