class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<long long,long long>s;
        long long mini=0;
        for(int i=n-1;i>=0;i--){
            unordered_map<long long,long long>s1;
            for(auto x:s){
                long long c=(x.first&nums[i]);
                s1[c]+=x.second;
            }
            s1[nums[i]]++;
            for(auto x:s1){
                // mini=min(mini,abs(k-x));
                if(x.first==k) mini+=x.second;
                // cout << mini << " ";
            }
            // s1.shrink_to_fit();
            // cout << s1.capacity() << " ";
            s=s1;
            // s.shrink_to_fit();
            // cout << s.capacity() << " ";

        }
        return mini;
    }
};
