class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
         long long total = 0;
        unordered_map<long long, long long> previousResults, currentResults;

        for (int num : array) {
            currentResults.clear();
            if (num == target) ++total;
            currentResults[num] = 1;

            for (const auto& [value, frequency] : previousResults) {
                long long newAndResult = value & num;
                if (newAndResult == target) total += frequency;
                currentResults[newAndResult] += frequency;
            }

            swap(previousResults, currentResults);
        }

        return total;
    }
};
