class Solution {
public:
    long long biyaniayush(const vector<int>& nums, int idx, int target, unordered_map<long long, long long>& prevRes) {
        long long count = 0;
        unordered_map<long long, long long> biyani;
        if (nums[idx] == target) {
            count++;
        }
        biyani[nums[idx]] = 1;
        for (auto &[val, freq] : prevRes) {
            long long newAnd = val & nums[idx];
            if (newAnd == target) {
                count += freq;                
            }
            biyani[newAnd] += freq;
        }
        return count;
    }
    void kumarbiyani(unordered_map<long long, long long>& prevRes, int num) {
        unordered_map<long long, long long> biyani;
        for (auto &[val, freq] : prevRes) {
            biyani[val & num] += freq;
        }
        biyani[num]++;
        prevRes = biyani;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long, long long> prevRes;
        long long total = 0;
        for (int i = 0; i < nums.size(); ++i) {
            total += biyaniayush(nums, i, k, prevRes);
            kumarbiyani(prevRes, nums[i]);
        }
        return total;
    }
};
