using ll=long long;
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int k) {
      ll n =nums.size();
       ll ans=0;
        map<ll,ll>mp;
        for(int i=0;i<n;i++){
            map<ll,ll>mp2; 
            if(nums[i]==k)ans++;
            mp2[nums[i]]=1;
            for(auto it:mp){
                ll numm=it.first&nums[i];
                if(numm==k)ans+=it.second;
                mp2[numm]+=it.second;
            }
            mp=mp2;
        }
        return ans; 
    }
};
