from collections import defaultdict
from typing import List

class Solution:
    def countSubarrays(self, array: List[int], target: int) -> int:
        size = len(array)
        total = 0
        previousResults = defaultdict(int)

        for index in range(size):
            currentResults = defaultdict(int)
            if array[index] == target:
                total += 1
            currentResults[array[index]] = 1

            for value, frequency in previousResults.items():
                newAndResult = value & array[index]
                if newAndResult == target:
                    total += frequency
                currentResults[newAndResult] += frequency

            previousResults = currentResults

        return total
