class Solution {
public:

    #define ll long long int
    long long countSubarrays(vector<int>& pp, int k) {

    int n = pp.size();

    ll cnt= 0;

    unordered_map<ll, ll> pr;

        for(int i=0; i<n; ++i){
             unordered_map< ll,ll> cr;

            if(pp[i]==k){
                ++cnt;

            }
            cr[pp[i]] = 1;

            for(auto & [value, f] : pr){

                ll neww = value&pp[i];
                if(neww == k){

                     cnt+=f;
                }
                cr[neww] += f;
            }
            pr = cr;
        }
        return cnt;
    }
};
