#define ll long long int
class Solution {
public:
    ll countSubarrays(vector<int >& arr, int  t) {
        ll  n = arr.size();
        ll ans8 = 0;
        ll ans = (1LL << 32) - 1;
        vector<ll> temp(32, 0);

        for (ll  i = 0; i < n; ++i) {
            ans &= arr[i];
            bool d = true;
            ll ans1=i+1,ans2=0;
            for (ll  j = 0; j < 32; ++j) {
                if (arr[i] & (1 << j))
                temp[j]++;
                else
                temp[j] = 0;

                bool ans3 = t & (1 << j);
                bool ans4 = ans & (1 << j);

                if (!ans3 && ans4)
                d = false;
                else if (ans3 && !ans4) 
                ans1 = min(ans1, temp[j]);
                else if (!ans3 && !ans4)
                ans2 = max(ans2, temp[j]);
            }
            if(d){
                if(ans2<ans1)
                ans8+=(ans1-ans2);
            }
        }

        return ans8;
    }
};
