#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int X) {
        ll res = 0;
        int n = nums.size();


        unordered_map<ll, ll> prev;

        for (int i = 0; i < n; ++i) {
            unordered_map<ll, ll> temp; 

            if (nums[i] == X) 
                ++res;

            temp[nums[i]] = 1;

            for (auto& [a, b] : prev) {
                ll z = a & nums[i];
                if (z == X) {
                    res += b;
                }
                temp[z] += b;
            }

            prev = temp;
        }
        return res;
    }
};
