class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long count = 0, n = nums.size();
        unordered_map<int, int> freq, nfreq;
        for (int i = 0; i < n; i++) {
            nfreq.clear();
            for (auto& [val, cnt] : freq) 
                nfreq[val & nums[i]] += cnt;
            nfreq[nums[i]]++;
            freq = nfreq;
            if (freq.find(k) != freq.end())
                count += freq[k];
        }
        return count;
    }
};
