class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int curr;
        long ct=0;
        long iv=2147483647;
        unordered_map<int,int>mp1,mp2;
        // for(int i=0;i<32;i++)iv+=(1<<i);
        // cout<<iv<<endl;
        for(int i=0;i<nums.size();i++){
            mp2.clear();
            curr=nums[i];
            mp2[curr]++;
            if(nums[i]==k)ct++;
            for(auto &it:mp1){
                if((curr&it.first)==k)ct+=it.second;
                mp2[curr&it.first]+=it.second;
                // // cout<<curr<<" ";
                // if(curr<k)break;
                // else if(curr==k)ct++;
            }
            mp1=mp2;
            // cout<<endl;
        }
        return ct;
    }
};
