class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n =nums.size();
        long long ans=0;

        unordered_map<long long,long long>mp;

        for (int i=0; i<n; i++) {
            unordered_map<long long,long long>mp2; 

            if (nums[i] == k) 
            {
                ans++;
            }
            mp2[nums[i]] = 1;

            for (auto it: mp) 
            {
                long long cures = it.first & nums[i];
                if (cures == k) 
                {
                    ans += it.second;
                }
                mp2[cures] += it.second;
            }

            mp = mp2;
        }

        return ans;
    }
};
