class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0

        # Dictionary to store the bitwise AND results of subarrays ending at each index
        dp = {}

        for i in range(n):
            new_dp = {}

            # Update the DP dictionary for the current element
            if nums[i] == k:
                count += 1
            new_dp[nums[i]] = new_dp.get(nums[i], 0) + 1

            for prev_and in dp:
                current_and = prev_and & nums[i]
                if current_and == k:
                    count += dp[prev_and]
                if current_and >= k:
                    new_dp[current_and] = new_dp.get(current_and, 0) + dp[prev_and]

            dp = new_dp

        return count
