class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        long long ans=0;

        unordered_map<long long, long long>mp;
        for (auto num: arr) {
            unordered_map<long long, long long> mp2; 
            mp2[num]=1;

            if (num==k) ans++;

            for (auto it: mp) {
                long long res2=(it.first&num);
                if (res2==k) ans+=it.second;
                mp2[res2]+=it.second;
            }

            mp = mp2;
        }
        return ans;
    }
};
