class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        int n = nums.size();
        long long result = 0;
        unordered_map<long long, long long> prevMap;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currentMap;

            if (nums[i] == target) {
                ++result;
            }

            currentMap[nums[i]] = 1;

            for (auto& entry : prevMap) {
                long long newAndValue = entry.first & nums[i];

                if (newAndValue == target) {
                    result += entry.second;
                }

                currentMap[newAndValue] += entry.second;
            }

            prevMap = currentMap;
        }

        return result;
    }
};
