class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        int n=nums.size();
        unordered_map<long long,long long> prev;
        for(int i=0;i<n;i++){
            long long currVal=nums[i];
            long long currFreq=1;
            unordered_map<long long,long long>curr;
            curr[currVal]=currFreq;
            if(currVal==k) ans++;
            for(auto ele:prev){
                long long andRes=ele.first&currVal;
                if(andRes==k) ans+=ele.second;
                curr[andRes]+=ele.second;
            }
            prev=curr;
        }
        return ans;

    }
};
