class Solution {
    public long countSubarrays(int[] nums, int k) {
         int size = nums.length;
        long ans = 0;
        HashMap<Long, Long>  prev = new HashMap<>();
        for (int index = 0; index < size; ++index) {
            HashMap<Long, Long>  curr = new HashMap<>(); 
            if (nums[index] == k) {
                ++ans;
            }
            curr.put((long) nums[index], 1L);
            for (var entry : prev.entrySet()) {
                long nr = entry.getKey() & nums[index];
                if (nr == k) {
                    ans += entry.getValue();
                }
                curr.merge((long) nr, entry.getValue(), Long::sum);
            }

            prev = curr;
        }
        return ans;
    }
}
