class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        std::unordered_map<int, int> andCount;
    long long count = 0;

    for (int num : nums) {
        std::unordered_map<int, int> newAndCount;

        if (num == k) {
            count++;
        }

        newAndCount[num] = 1;

        for (const auto& [currentAnd, freq] : andCount) {
            int newAnd = currentAnd & num;
            newAndCount[newAnd] += freq;
            if (newAnd == k) {
                count += freq;
            }
        }

        andCount = std::move(newAndCount);
    }

    return count;
    }
};
