class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<int,long long> s;
        map<int,long long> ans;
        for (int& x : nums) {
            map<int,long long> t;
            t[x] = 1;
            for(auto& [key, value] : s) {
                t[key & x] += value;
            }
            s = t;
            for(auto& [key, value] : s) {
                ans[key] += value;
            }
        }
        return  ans[k];

    }
};
